CustomField.formula:
IF(CCMI__Completed_Date__c <= CCMI__Desired_Start__c || CCMI__Completed_Date__c <= CCMI__Parent_End__c,
  CCMI__Completed_Date__c,
  IF(ISBLANK(CCMI__Parent_End__c) || CCMI__Desired_Start__c > CCMI__Parent_End__c,
    /* Use desired start if after parent end (or if there is no parent) */
    CCMI__Desired_Start__c,
    /* Otherwise, set to Parent End + 1, adjusted for weekends */
    CCMI__Parent_End__c + CASE(MOD(CCMI__Parent_End__c - DATE(1900, 1, 8), 7),
      4, 3,
      5, 2,
      1
    )
  )
)
